    public $request_method = 'POST';
    public $protocol_version = '1.0';
    public $outgoing_headers = [];
    public $incoming_headers = [];
    public $outgoing_payload = '';
    public $incoming_payload = '';
    public $persistentConnection = false;
    public $use_curl = false;
    public $proxy = null;
    private $connection;
    private $curl_handle;

    public function __construct($url, $curl_options = null, $use_curl = false) {
        $this->url = $url;
        $this->use_curl = $use_curl;
        $parsed_url = parse_url($url);
        $this->scheme = $parsed_url['scheme'];
        $this->host = $parsed_url['host'];
        $this->port = isset($parsed_url['port']) ? $parsed_url['port'] : ($this->scheme === 'https' ? 443 : 80);
        $this->path = isset($parsed_url['path']) ? $parsed_url['path'] : '/';
        $this->uri = $parsed_url['path'] . (isset($parsed_url['query']) ? '?' . $parsed_url['query'] : '');

        if ($this->use_curl && is_array($curl_options)) {
            $this->curl_handle = curl_init();
            foreach ($curl_options as $option => $value) {
                curl_setopt($this->curl_handle, $option, $value);
            }
        }
    }

    public function connect($connection_timeout = 0, $response_timeout = 30) {
        if ($this->use_curl) {
            return true;
        } else {
            $errno = 0;
            $errstr = '';
            $this->connection = fsockopen($this->host, $this->port, $errno, $errstr, $connection_timeout);
            if (!$this->connection) {
                throw new Exception("Failed to connect: $errstr ($errno)");
            }
            stream_set_timeout($this->connection, $response_timeout);
            return true;
        }
    }

    public function send($data, $timeout = 0, $response_timeout = 30, $cookies = null) {
        $this->outgoing_payload = $data;
        if ($cookies) {
            $this->setHeader('Cookie', implode('; ', $cookies));
        }
        $this->sendRequest($data, $cookies);
        return $this->getResponse();
    }

    public function sendRequest($data, $cookies = null) {
        $request = "{$this->request_method} {$this->uri} HTTP/{$this->protocol_version}\r\n";
        foreach ($this->outgoing_headers as $name => $value) {
            $request .= "$name: $value\r\n";
        }
        if ($cookies) {
            $request .= "Cookie: " . implode('; ', $cookies) . "\r\n";
        }
        $request .= "Host: {$this->host}\r\n";
        $request .= "Content-Length: " . strlen($data) . "\r\n";
        $request .= "Connection: " . ($this->persistentConnection ? 'Keep-Alive' : 'Close') . "\r\n";
        $request .= "\r\n";
        $request .= $data;

        if ($this->use_curl) {
            curl_setopt($this->curl_handle, CURLOPT_URL, $this->url);
            curl_setopt($this->curl_handle, CURLOPT_RETURNTRANSFER, true);
            curl_setopt($this->curl_handle, CURLOPT_POSTFIELDS, $data);
            curl_setopt($this->curl_handle, CURLOPT_HTTPHEADER, $this->outgoing_headers);
            if ($cookies) {
                curl_setopt($this->curl_handle, CURLOPT_COOKIE, implode('; ', $cookies));
            }
            curl_setopt($this->curl_handle, CURLOPT_TIMEOUT, $timeout);
            curl_setopt($this->curl_handle, CURLOPT_CONNECTTIMEOUT, $response_timeout);
        } else {
            fwrite($this->connection, $request);
        }
    }

    public function getResponse() {
        if ($this->use_curl) {
            $this->incoming_payload = curl_exec($this->curl_handle);
            $this->incoming_headers = curl_getinfo($this->curl_handle, CURLINFO_HEADER_OUT);
            return $this->incoming_payload;
        } else {
            $response = '';
            while (!feof($this->connection)) {
                $response .= fgets($this->connection, 1024);
            }
            list($headers, $body) = explode("\r\n\r\n", $response, 2);
            $this->incoming_headers = $this->parseHeaders($headers);
            $this->incoming_payload = $body;
            return $body;
        }
    }

    public function setHeader($name, $value) {
        $this->outgoing_headers[$name] = $value;
    }

    public function setCurlOption($option, $value) {
        if ($this->use_curl) {
            curl_setopt($this->curl_handle, $option, $value);
        }
    }

    public function parseCookie($cookie_str) {
        $cookies = [];
        $parts = explode('; ', $cookie_str);
        foreach ($parts as $part) {
            list($name, $value) = explode('=', $part, 2);
            $cookies[$name] = $value;
        }
        return $cookies;
    }

    private function parseHeaders($headers) {
        $header_list = [];
        $headers = preg_split('/\r\n/', $headers);
        foreach ($headers as $header) {
            if (strpos($header, ':') !== false) {
                list($name, $value) = explode(': ', $header, 2);
                $header_list[$name] = $value;
            }
        }
        return $header_list;
    }
}
