    $url = isset($params['file']) ? $params['file'] : '';
    $assign = isset($params['assign']) ? $params['assign'] : null;
    $assignHeaders = isset($params['assign_headers']) ? $params['assign_headers'] : null;
    $user = isset($params['user']) ? $params['user'] : '';
    $pass = isset($params['pass']) ? $params['pass'] : '';
    $accept = isset($params['accept']) ? $params['accept'] : '*/*';
    $headers = isset($params['header']) ? $params['header'] : [];
    $proxyHost = isset($params['proxy_host']) ? $params['proxy_host'] : '';
    $proxyPort = isset($params['proxy_port']) ? $params['proxy_port'] : 8080;
    $agent = isset($params['agent']) ? $params['agent'] : 'PHP';
    $referer = isset($params['referer']) ? $params['referer'] : '';
    $timeout = isset($params['timeout']) ? $params['timeout'] : 30;

    if (empty($url)) {
        trigger_error('URL is required', E_USER_NOTICE);
        return;
    }

    // Parse URL
    $parsedUrl = parse_url($url);
    $host = isset($parsedUrl['host']) ? $parsedUrl['host'] : '';
    $port = isset($parsedUrl['port']) ? $parsedUrl['port'] : 80;
    $path = isset($parsedUrl['path']) ? $parsedUrl['path'] : '/';
    $query = isset($parsedUrl['query']) ? '?' . $parsedUrl['query'] : '';

    // Use proxy if configured
    if (!empty($proxyHost)) {
        $host = $proxyHost;
        $port = $proxyPort;
        $path = $url;
    }

    // Create socket
    $socket = fsockopen($host, $port, $errno, $errstr, $timeout);
    if (!$socket) {
        trigger_error("Failed to connect to $host:$port ($errno: $errstr)", E_USER_NOTICE);
        return;
    }

    // Prepare request headers
    $request = "GET $path HTTP/1.1\r\n";
    $request .= "Host: " . (empty($proxyHost) ? $host : $parsedUrl['host']) . "\r\n";
    $request .= "User-Agent: $agent\r\n";
    $request .= "Accept: $accept\r\n";
    if (!empty($referer)) {
        $request .= "Referer: $referer\r\n";
    }
    if (!empty($user) && !empty($pass)) {
        $request .= "Authorization: Basic " . base64_encode("$user:$pass") . "\r\n";
    }
    if (is_array($headers)) {
        foreach ($headers as $header) {
            $request .= "$header\r\n";
        }
    } elseif (is_string($headers)) {
        $request .= "$headers\r\n";
    }
    $request .= "Connection: Close\r\n\r\n";

    // Send request
    fwrite($socket, $request);

    // Read response
    $response = '';
    $headers = [];
    $inHeaders = true;
    while (!feof($socket)) {
        $line = fgets($socket, 4096);
        if ($inHeaders && trim($line) == '') {
            $inHeaders = false;
        }
        if ($inHeaders) {
            $headers[] = $line;
        } else {
            $response .= $line;
        }
    }
    fclose($socket);

    // Extract content
    $content = substr($response, strpos($response, "\r\n\r\n") + 4);

    // Assign to template if needed
    if ($assign) {
        $template->assign($assign, $content);
    }
    if ($assignHeaders) {
        $template->assign($assignHeaders, $headers);
    }

    // Return content if not assigned
    if (!$assign) {
        return $content;
    }
}
