            return '';
        }

        $host = $url['host'];
        $port = isset($url['port']) ? $url['port'] : 80;
        $path = isset($url['path']) ? $url['path'] : '/';
        $query = isset($url['query']) ? '?' . $url['query'] : '';

        $fp = null;
        if ($conf['ip']) {
            $fp = fsockopen($conf['ip'], $port, $errno, $errstr, $conf['timeout']);
        } else {
            $fp = fsockopen($host, $port, $errno, $errstr, $conf['timeout']);
        }

        if (!$fp) {
            return '';
        }

        $out = "GET $path$query HTTP/1.1\r\n";
        $out .= "Host: $host\r\n";
        if ($conf['cookie']) {
            $out .= "Cookie: " . $conf['cookie'] . "\r\n";
        }
        $out .= "Connection: Close\r\n\r\n";

        if ($conf['post']) {
            $out = "POST $path$query HTTP/1.1\r\n";
            $out .= "Host: $host\r\n";
            if ($conf['cookie']) {
                $out .= "Cookie: " . $conf['cookie'] . "\r\n";
            }
            $out .= "Content-Type: application/x-www-form-urlencoded\r\n";
            $out .= "Content-Length: " . strlen($conf['post']) . "\r\n";
            $out .= "Connection: Close\r\n\r\n";
            $out .= $conf['post'];
        }

        fwrite($fp, $out);

        $response = '';
        $contentLength = null;
        $receivedLength = 0;

        while (!feof($fp)) {
            $line = fgets($fp, 1024);
            if (stripos($line, 'Content-Length:') === 0) {
                $contentLength = (int)trim(substr($line, 15));
            }
            if ($line == "\r\n") {
                break;
            }
        }

        while (!feof($fp)) {
            $data = fread($fp, 1024);
            if ($conf['limit'] && ($receivedLength + strlen($data)) > $conf['limit']) {
                $data = substr($data, 0, $conf['limit'] - $receivedLength);
            }
            $response .= $data;
            $receivedLength += strlen($data);
            if ($conf['limit'] && $receivedLength >= $conf['limit']) {
                break;
            }
            if ($contentLength !== null && $receivedLength >= $contentLength) {
                break;
            }
        }

        fclose($fp);

        return $response;
    }
}
