use Psr\Log\NullLogger;

class SocketHttpClient implements \Psr\Http\Client\ClientInterface
{
    private $logger;

    public function __construct(LoggerInterface $logger = null)
    {
        $this->logger = $logger ?? new NullLogger();
    }

    public function sendRequest(RequestInterface $request): ResponseInterface
    {
        $uri = $request->getUri();
        $scheme = $uri->getScheme();
        $host = $uri->getHost();
        $port = $uri->getPort() ?: ($scheme === 'https' ? 443 : 80);
        $path = $uri->getPath() ?: '/';
        $query = $uri->getQuery();
        if ($query) {
            $path .= '?' . $query;
        }
        $method = $request->getMethod();
        $headers = $request->getHeaders();
        $body = $request->getBody();

        $this->logger->debug('Sending request', [
            'method' => $method,
            'uri' => (string)$uri,
            'headers' => $headers,
        ]);

        $socket = $this->createSocket($scheme, $host, $port);

        $requestString = $this->buildRequestString($method, $path, $headers, $body);
        fwrite($socket, $requestString);

        $response = $this->readResponse($socket);

        fclose($socket);

        return $response;
    }

    private function createSocket(string $scheme, string $host, int $port)
    {
        if ($scheme === 'https') {
            $context = stream_context_create([
                'ssl' => [
                    'verify_peer' => true,
                    'verify_peer_name' => true,
                ],
            ]);
            $socket = stream_socket_client("ssl://{$host}:{$port}", $errno, $errstr, 30, STREAM_CLIENT_CONNECT, $context);
        } else {
            $socket = fsockopen($host, $port, $errno, $errstr, 30);
        }

        if (!$socket) {
            $this->logger->error('Failed to connect to server', [
                'host' => $host,
                'port' => $port,
                'error' => $errstr,
                'errno' => $errno,
            ]);
            throw new \RuntimeException("Failed to connect to {$host}:{$port} - {$errstr}");
        }

        return $socket;
    }

    private function buildRequestString(string $method, string $path, array $headers, $body): string
    {
        $request = "{$method} {$path} HTTP/1.1\r\n";
        $request .= "Host: {$headers['Host'][0]}\r\n";

        foreach ($headers as $name => $values) {
            foreach ($values as $value) {
                $request .= "{$name}: {$value}\r\n";
            }
        }

        $request .= "\r\n";
        $request .= $body;

        return $request;
    }

    private function readResponse($socket): ResponseInterface
    {
        $response = '';
        while (!feof($socket)) {
            $response .= fgets($socket, 1024);
        }

        $this->logger->debug('Received response', [
            'response' => $response,
        ]);

        // Parse the response
        list($statusLine, $headers, $body) = $this->parseResponse($response);

        return new \GuzzleHttp\Psr7\Response(
            $statusLine['code'],
            $headers,
            $body
        );
    }

    private function parseResponse(string $response): array
    {
        list($statusLine, $headers, $body) = preg_split('/\r?\n\r?\n/', $response, 3);

        $statusLineParts = explode(' ', $statusLine, 3);
        $statusLine = [
            'version' => $statusLineParts[0],
            'code' => (int)$statusLineParts[1],
            'message' => $statusLineParts[2] ?? '',
        ];

        $parsedHeaders = [];
        foreach (explode("\r\n", $headers) as $header) {
            if (strpos($header, ': ') !== false) {
                list($name, $value) = explode(': ', $header, 2);
                $parsedHeaders[$name] = [$value];
            }
        }

        return [$statusLine, $parsedHeaders, $body];
    }
}
