<?php
function rabbitmqConsumer($host, $port, $user, $pass, $vhost, $exchange, $queue, $consumerTag) {
    // Create a new AMQP connection
    $connection = new AMQPStreamConnection($host, $port, $user, $pass, $vhost);
    $channel = $connection->channel();

    // Declare the exchange
    $channel->exchange_declare($exchange, 'direct', false, true, false);

    // Declare the queue
    list($queueName,,) = $channel->queue_declare($queue, false, true, false, false);

    // Bind the queue to the exchange
    $channel->queue_bind($queueName, $exchange);

    // Define the callback function to process messages
    $callback = function ($msg) use ($channel) {
        echo "Received message: " . $msg->body . "\n";
        if ($msg->body === 'quit') {
            echo "Quit message received. Stopping consumer.\n";
            $channel->basic_cancel($msg->delivery_info['consumer_tag']);
        }
        $channel->basic_ack($msg->delivery_info['delivery_tag']);
    };

    // Start consuming messages
    $channel->basic_consume($queueName, $consumerTag, false, false, false, false, $callback);

    // Loop until the consumer stops
    while ($channel->is_consuming()) {
        $channel->wait();
    }

