use PhpAmqpLib\Connection\AMQPStreamConnection;
use PhpAmqpLib\Message\AMQPMessage;

function send_delayed_message($message_content, $delay_ms) {
    // Load RabbitMQ configuration
    $host = HOST;
    $port = PORT;
    $user = USER;
    $pass = PASS;
    $vhost = VHOST;

    // Create connection
    $connection = new AMQPStreamConnection($host, $port, $user, $pass, $vhost);
    $channel = $connection->channel();

    // Declare a delayed exchange
    $exchange_name = 'delayed_exchange';
    $channel->exchange_declare($exchange_name, 'x-delayed-message', false, true, false, false, false, null, ['x-delayed-type' => 'direct']);

    // Declare a queue
    $queue_name = 'delayed_queue';
    $channel->queue_declare($queue_name, false, true, false, false);

    // Bind the queue to the exchange
    $channel->queue_bind($queue_name, $exchange_name);

    // Create a message with delay header
    $msg = new AMQPMessage(
        $message_content,
        [
            'content_type' => 'text/plain',
            'delivery_mode' => 2, // make message persistent
            'headers' => ['x-delay' => $delay_ms]
        ]
    );

    // Publish the message
    $channel->basic_publish($msg, $exchange_name);

    // Close the channel and connection
    $channel->close();
    $connection->close();
}
