function publishMessagesToRabbitMQ($msg_count, $batch_size, $host, $port, $user, $pass, $vhost) {
    $start_time = microtime(true);

    $connection = new AMQPStreamConnection($host, $port, $user, $pass, $vhost);
    $channel = $connection->channel();

    $exchange_name = 'test_exchange';
    $queue_name = 'test_queue';

    $channel->exchange_declare($exchange_name, 'direct', false, true, false);
    list($queue_name, ,) = $channel->queue_declare($queue_name, false, true, false, false);
    $channel->queue_bind($queue_name, $exchange_name);

    $messages_sent = 0;
    while ($messages_sent < $msg_count) {
        $batch_end = min($messages_sent + $batch_size, $msg_count);
        for ($i = $messages_sent; $i < $batch_end; $i++) {
            $message = "Message $i";
            $channel->basic_publish(new AMQPMessage($message), $exchange_name);
        }
        $messages_sent += $batch_size;
    }

    // Send quit message
    $channel->basic_publish(new AMQPMessage('quit'), $exchange_name);

    $channel->close();
    $connection->close();

    $end_time = microtime(true);
    return $end_time - $start_time;
}
