class CI_Session_redis_driver extends CI_Session_driver implements SessionHandlerInterface {
    protected $_redis;
    protected $_key_prefix = 'ci_session:';
    protected $_lock_key;
    protected $_key_exists = FALSE;
    protected $_config = [];
    protected $_session_id;
    protected $_fingerprint;

    public function __construct(&$params) {
        parent::__construct($params);
        $this->_config = $params['config'];
    }

    public function open($save_path, $name) {
        $this->_redis = new Redis();
        return $this->_redis->connect($this->_config['host'], $this->_config['port']);
    }

    public function read($session_id) {
        $this->_session_id = $session_id;
        $this->_lock_key = $this->_key_prefix . 'lock:' . $session_id;
        $this->_key_exists = $this->_redis->exists($this->_key_prefix . $session_id);

        if ($this->_key_exists && $this->_get_lock($session_id)) {
            $data = $this->_redis->get($this->_key_prefix . $session_id);
            $this->_fingerprint = md5($data);
            return $data;
        }
        return '';
    }

    public function write($session_id, $session_data) {
        if ($this->_session_id !== $session_id) {
            if (!$this->read($session_id)) {
                return $this->_fail();
            }
        }

        if ($this->_fingerprint === md5($session_data)) {
            return $this->_success();
        }

        if ($this->_redis->setex($this->_key_prefix . $session_id, $this->_config['expiration'], $session_data)) {
            $this->_fingerprint = md5($session_data);
            return $this->_success();
