<?php

interface OutputInterface {
    public function log($message);
}

class Response {
    private $status;
    private $payload;

    public function __construct($status, $payload) {
        $this->status = $status;
        $this->payload = $payload;
    }

    public function getStatus() {
        return $this->status;
    }

    public function getPayload() {
        return $this->payload;
    }
}

class Server {
    private $host;
    private $port;
    private $output;
    private $stop = false;
    private $socket;
    private $clientSockets = [];
    private $afterCallback;
    private $tickerCallback;
    private $routerCallback;

    public function __construct(string $host, int $port, OutputInterface $output) {
        $this->host = $host;
        $this->port = $port;
        $this->output = $output;
        $this->checkRequiredExtensionsExists();
    }

    private function checkRequiredExtensionsExists() {
        if (!extension_loaded('sockets')) {
            throw new Exception('The sockets extension is required.');
        }
    }

    public function run() {
        $this->socket = $this->createServerSocket();
        $this->updatePort();
        $this->output->log("Server started on {$this->host}:{$this->port}");

        if ($this->afterCallback) {
            call_user_func($this->afterCallback);
        }

        while (!$this->stop) {
            $read = array_merge([$this->socket], $this->clientSockets);
            $write = null;
            $except = null;

            if (stream_select($read, $write, $except, 0)) {
                if (in_array($this->socket, $read)) {
                    $this->acceptNewConnections();
                }

                foreach ($this->clientSockets as $key => $clientSocket) {
                    if (in_array($clientSocket, $read)) {
                        $this->handleClientRequests($clientSocket, $key);
                    }
                }
            }

            if ($this->tickerCallback) {
                call_user_func($this->tickerCallback);
            }
        }

        $this->output->log("Server stopped.");
    }

    private function createServerSocket() {
        $address = "tcp://{$this->host}:{$this->port}";
        $socket = stream_socket_server($address, $errno, $errstr);

        if (!$socket) {
            throw new Exception("Failed to create socket: $errstr ($errno)");
        }

        stream_set_blocking($socket, 0);
        return $socket;
    }

    private function updatePort() {
        $address = stream_socket_get_name($this->socket, false);
        preg_match('/:(\d+)$/', $address, $matches);
        if (isset($matches[1])) {
            $this->port = (int)$matches[1];
        }
    }

    private function acceptNewConnections() {
        $clientSocket = @stream_socket_accept($this->socket, 0);
        if ($clientSocket) {
            stream_set_blocking($clientSocket, 0);
            $this->clientSockets[] = $clientSocket;
            $this->output->log("New client connected.");
        }
    }

    private function handleClientRequests($clientSocket, $key) {
        $request = $this->readClientRequest($clientSocket);
        if ($request) {
            $parsedRequest = $this->parseRequest($request);
            $response = new Response(200, "Received: " . json_encode($parsedRequest));
            $this->sendResponse($clientSocket, $response);
        } else {
            $this->closeClientSocket($clientSocket, $key);
        }
    }

    private function readClientRequest($clientSocket) {
        $buffer = '';
        do {
            $part = fread($clientSocket, 4096);
            if ($part === false || $part === '') {
                return false;
            }
            $buffer .= $part;
        } while (!feof($clientSocket));

        return $buffer;
    }

    private function parseRequest($request) {
        // Simple parsing for demonstration
        $lines = explode("\r\n", $request);
        $firstLine = explode(' ', $lines[0]);
        $path = $firstLine[1];
        $payload = trim($request, "\r\n");
        return ['path' => $path, 'payload' => $payload];
    }

    private function sendResponse($clientSocket, Response $response) {
        $statusLine = "HTTP/1.1 " . $response->getStatus() . " OK\r\n";
        $headers = "Content-Type: application/json\r\n";
        $body = json_encode(['status' => $response->getStatus(), 'data' => $response->getPayload()]);
        $contentLength = "Content-Length: " . strlen($body) . "\r\n";
        $responseString = $statusLine . $headers . $contentLength . "\r\n" . $body;

        fwrite($clientSocket, $responseString);
    }

    private function closeClientSocket($clientSocket, $key) {
        fclose($clientSocket);
        unset($this->clientSockets[$key]);
        $this->output->log("Client disconnected.");
    }

    public function afterRun(Closure $callback) {
        $this->afterCallback = $callback;
    }

    public function ticker(Closure $callback) {
        $this->tickerCallback = $callback;
    }

    public function router(Closure $callback) {
        $this->routerCallback = $callback;
    }

    public function stop() {
        $this->stop = true;
    }
}
