<?php

function generateImageFromDot($dot_script, $type) {
    // Validate input
    if (!in_array(strtolower($type), ['png', 'svg', 'jpg'])) {
        fwrite(STDERR, "Invalid image type. Supported types: png, svg, jpg\n");
        exit(1);
    }

    // Prepare command
    $command = sprintf('dot -T%s', escapeshellarg($type));

    // Open process
    $descriptorspec = [
        0 => ["pipe", "r"],  // stdin is a pipe that the child will read from
        1 => ["pipe", "w"],  // stdout is a pipe that the child will write to
        2 => ["pipe", "w"]   // stderr is a pipe that the child will write to
    ];

    $process = proc_open($command, $descriptorspec, $pipes);

    if (is_resource($process)) {
        // Write DOT script to stdin
        fwrite($pipes[0], $dot_script);
        fclose($pipes[0]);

        // Read stdout (image data)
        $imageData = stream_get_contents($pipes[1]);
        fclose($pipes[1]);

        // Read stderr (error messages)
        $errorData = stream_get_contents($pipes[2]);
        fclose($pipes[2]);

        // Close process
        $return_value = proc_close($process);

        // Check for errors
        if ($return_value !== 0 || !empty($errorData)) {
            fwrite(STDERR, "Error generating image: " . $errorData);
            exit(1);
        }

        return $imageData;
    } else {
        fwrite(STDERR, "Failed to open process for command: $command\n");
        exit(1);
    }
}

