    // Prepare the command
    $command = 'git describe --tags';

    // Initialize variables for subprocess communication
    $descriptorspec = array(
       0 => array("pipe", "r"),  // stdin is a pipe that the child will read from
       1 => array("pipe", "w"),  // stdout is a pipe that the child will write to
       2 => array("pipe", "w")   // stderr is a pipe that the child will write to
    );

    // Start the subprocess
    $process = proc_open($command, $descriptorspec, $pipes, $path);

    // Check if the subprocess was successfully created
    if (is_resource($process)) {
        // Close the stdin pipe since we're not sending any input
        fclose($pipes[0]);

        // Read the output from stdout and stderr
        $output = stream_get_contents($pipes[1]);
        $error = stream_get_contents($pipes[2]);

        // Close the pipes
        fclose($pipes[1]);
        fclose($pipes[2]);

        // Get the exit code of the subprocess
        $return_value = proc_close($process);

        // Check if the command was successful
        if ($return_value === 0) {
            // Trim and return the output
            return trim($output);
        }
    }

    // Return false if unsuccessful
