<?php

namespace App\HttpClient;

use Symfony\Component\HttpFoundation\Request;
use Symfony\Component\HttpFoundation\Response;
use Symfony\Contracts\HttpClient\HttpClientInterface;
use Symfony\Contracts\HttpClient\ResponseInterface;
use Symfony\Component\HttpClient\Parts\AbstractPart;
use Symfony\Component\HttpClient\Parts\FormDataPart;
use Symfony\Component\HttpClient\Parts\TextPart;
use Symfony\Component\HttpKernel\HttpKernelInterface;

class HttpClientKernel implements HttpKernelInterface
{
    private HttpClientInterface $client;

    public function __construct(HttpClientInterface $client = null)
    {
        $this->client = $client ?? HttpClient::create();
    }

    public function handle(Request $request, int $type = self::MAIN_REQUEST, bool $catch = true): Response
    {
        $method = $request->getMethod();
        $uri = $request->getUri();
        $headers = $this->getHeaders($request);
        $body = $this->getBody($request);

        $options = [
            'headers' => $headers,
        ];

        if ($body !== null) {
            $options['body'] = $body;
        }

        try {
            /** @var ResponseInterface $response */
            $response = $this->client->request($method, $uri, $options);
            return new Response(
                $response->getContent(),
                $response->getStatusCode(),
                $response->getHeaders(false)
            );
        } catch (\Exception $e) {
            if (!$catch) {
                throw $e;
            }
            return new Response($e->getMessage(), 500);
        }
    }

    private function getBody(Request $request): ?AbstractPart
    {
        $method = $request->getMethod();
        if (in_array($method, ['GET', 'HEAD'], true)) {
            return null;
        }

        $content = $request->getContent();
        if ($content === '') {
            return null;
        }

        $contentType = $request->headers->get('Content-Type');
        if (stripos($contentType, 'application/x-www-form-urlencoded') !== false) {
            parse_str($content, $data);
            return new FormDataPart($data);
        }

        if (stripos($contentType, 'multipart/form-data') !== false) {
            parse_str($content, $data);
            return new FormDataPart($data);
        }

        return new TextPart($content, $contentType);
    }

    private function getHeaders(Request $request): array
    {
        $headers = $request->headers->allPreserveCase();

        if ($request->cookies->count() > 0) {
            $cookies = [];
            foreach ($request->cookies as $name => $value) {
                $cookies[] = "$name=$value";
            }
            $headers['Cookie'] = implode('; ', $cookies);
        }

        return $headers;
    }
}
