class QiniuStorage {
    const QINIU_RSF_HOST = 'https://rsf.qbox.me';
    const QINIU_RS_HOST = 'https://rs.qbox.me';
    const QINIU_UP_HOST = 'https://up.qbox.me';

    public $timeout = '30s';
    public $error = '';
    public $errorStr = '';

    public function request($path, $method, $headers = null, $body = null) {
        $url = $this->getHost($path) . $path;
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->timeout);

        if ($method === 'POST' || $method === 'PUT') {
            curl_setopt($ch, CURLOPT_POST, true);
            curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
        } elseif ($method === 'HEAD') {
            curl_setopt($ch, CURLOPT_NOBODY, true);
        }

        if ($headers) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        }

        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $error = curl_error($ch);
        curl_close($ch);

        if ($error) {
            $this->error = $error;
            return false;
        }

        if ($httpCode >= 200 && $httpCode < 300) {
            if ($method === 'GET') {
                return $response;
            }
            return $this->response($response);
        } else {
            $this->error($httpCode, $response);
            return false;
        }
    }

    private function getHost($path) {
        if (strpos($path, '/stat/') === 0 || strpos($path, '/move/') === 0 || strpos($path, '/copy/') === 0 || strpos($path, '/delete/') === 0) {
            return self::QINIU_RS_HOST;
        } elseif (strpos($path, '/buckets/') === 0 || strpos($path, '/items/') === 0) {
            return self::QINIU_RSF_HOST;
        } else {
            return self::QINIU_UP_HOST;
        }
    }

