        $this->appSecret = $config['appSecret'];
        $this->accessToken = isset($config['accessToken']) ? $config['accessToken'] : null;
        $this->fileUploadSupport = isset($config['fileUploadSupport']) ? $config['fileUploadSupport'] : false;
