class URLChecker {
    const POLL_INTERVAL_MS = 1000; // Default poll interval in milliseconds
    const CONNECT_TIMEOUT_MS = 500; // Default connect timeout in milliseconds

    private function getHTTPResponseCode(string $url): ?int {
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_NOBODY, true);
