class Image {
    const BASE_URL = "https://via.placeholder.com/";
    const FORMAT_JPG = "jpg";
    const FORMAT_JPEG = "jpeg";
    const FORMAT_PNG = "png";

    private $categories = [
        "abstract",
        "animals",
        "business",
        "cats",
        "city",
        "food",
        "nightlife",
        "fashion",
        "people",
        "nature",
        "sports",
        "technics",
        "transport"
    ];

    public function image(
        string|null $dir = null,
        int $width = 640,
        int $height = 480,
        string|null $category = null,
        bool $fullPath = true,
        bool $randomize = true,
        string|null $word = null,
        bool $gray = false,
        string $format = 'png'
    ): string|false {
        if (!in_array(strtolower($format), [self::FORMAT_JPG, self::FORMAT_JPEG, self::FORMAT_PNG])) {
            return false;
        }

        if ($category && !in_array($category, $this->categories)) {
            return false;
        }

        $url = $this->imageUrl($width, $height, $category, $randomize, $word, $gray, $format);

        if ($dir === null) {
            $dir = sys_get_temp_dir();
        }

        $filename = basename($url);
        $filePath = $dir . DIRECTORY_SEPARATOR . $filename;

        if (function_exists('curl_exec')) {
            $ch = curl_init($url);
            $fp = fopen($filePath, 'wb');
            curl_setopt($ch, CURLOPT_FILE, $fp);
            curl_setopt($ch, CURLOPT_HEADER, 0);
            curl_exec($ch);
            curl_close($ch);
            fclose($fp);
        } else {
            $fileData = file_get_contents($url);
            if ($fileData === false) {
                return false;
            }
            file_put_contents($filePath, $fileData);
        }

        return $fullPath ? $filePath : $filename;
    }

    public function imageUrl(
        int $width,
        int $height,
        string|null $category,
        bool $randomize,
        string|null $word,
        bool $gray,
        string $format
    ): string {
        $url = self::BASE_URL . "{$width}x{$height}";

        if ($category) {
            $url .= "/{$category}";
        }

        if ($randomize) {
            $url .= "?random";
        }

        if ($word) {
            $url .= "&text=" . urlencode($word);
        }

        if ($gray) {
            $url .= "&grayscale";
        }

        $url .= ".{$format}";

        return $url;
    }
}
