        $params = [
            'v' => '2.0',
            'app_key' => '1',
            'key' => $id,
            '_ksTS' => time() . '_' . mt_rand(1000, 9999),
            'callback' => 'jsonp' . mt_rand(1000, 9999),
            'r' => $this->getRequestType($type)
        ];

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($params));
        $response = curl_exec($ch);
        curl_close($ch);

        if ($response === false) {
            return false;
        }

        $response = preg_replace('/^jsonp\d+\(/', '', $response);
        $response = preg_replace('/\);$/i', '', $response);

        return json_decode($response, true);
    }

    private function getRequestType($type) {
        switch ($type) {
            case 0:
                return 'song/detail';
            case 1:
                return 'album/detail';
            case 2:
                return 'collect/detail';
            default:
                return '';
        }
    }
}
