            } else {
                $url .= '?' . http_build_query($fields);
                curl_setopt($this->ch, CURLOPT_URL, $url);
            }
        }

        if ($files) {
            $cfile = curl_file_create($files['path'], $files['type'], $files['name']);
            $fields[$files['field']] = $cfile;
            curl_setopt($this->ch, CURLOPT_POSTFIELDS, $fields);
        }

        curl_setopt($this->ch, CURLOPT_FOLLOWLOCATION, $allow_redirects);
        curl_setopt($this->ch, CURLOPT_COOKIE, implode('; ', $this->cookies));
        curl_setopt($this->ch, CURLOPT_HTTPHEADER, $this->rawheaders);

        if ($cert) {
            curl_setopt($this->ch, CURLOPT_SSL_VERIFYPEER, true);
            curl_setopt($this->ch, CURLOPT_SSL_VERIFYHOST, 2);
            curl_setopt($this->ch, CURLOPT_CAINFO, $cert);
        }

        $this->raw = curl_exec($this->ch);
        $this->info = curl_getinfo($this->ch);
        $this->status_code = $this->info['http_code'];

        if ($this->raw === false) {
            $this->error = curl_error($this->ch);
            return false;
        }

        list($this->head, $this->content) = $this->split_header_body();
        $this->headers = $this->get_response_headers($this->head);
        $this->get_response_cookies($this->head, parse_url($url, PHP_URL_HOST));

        return $this->content;
    }

    private function is_url($url) {
        return filter_var($url, FILTER_VALIDATE_URL) !== false;
    }

    public function get_cookies($domain = '') {
        if ($domain) {
            return isset($this->domain_cookies[$domain]) ? $this->domain_cookies[$domain] : [];
        }
        return $this->cookies;
    }

    private function get_response_headers($header) {
        $headers = [];
        foreach (explode("\r\n", $header) as $line) {
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $headers[trim($key)] = trim($value);
            }
        }
        return $headers;
    }

    private function get_response_cookies($header, $domain) {
        if (!isset($this->domain_cookies[$domain])) {
            $this->domain_cookies[$domain] = [];
        }
        preg_match_all('/Set-Cookie: (.*?)\s*$/im', $header, $matches);
        foreach ($matches[1] as $cookie) {
            parse_str(str_replace(';', '&', $cookie), $cookieData);
            $this->domain_cookies[$domain] = array_merge($this->domain_cookies[$domain], $cookieData);
        }
    }

    private function split_header_body() {
        $pos = strpos($this->raw, "\r\n\r\n");
        return [$pos !== false ? substr($this->raw, 0, $pos) : '', $pos !== false ? substr($this->raw, $pos + 4) : ''];
    }

    private function get_history($header) {
        if (preg_match('/HTTP\/1\.1 (\d{3})/', $header, $matches)) {
            return (int)$matches[1];
        }
        return 0;
    }

    private function get_mimetype($filepath) {
        return mime_content_type($filepath);
    }
