            $this->domain_cookies[$domain] = [];
        }
        preg_match_all('/Set-Cookie: (.*?)=(.*?);/', $header, $matches);
        if (count($matches[1]) > 0) {
            foreach ($matches[1] as $index => $name) {
                $this->domain_cookies[$domain][$name] = $matches[2][$index];
            }
