class Downloader {
    public static function start($url, $saveFile, $timeout = 10) {
        if (!is_array($url)) {
            $url = [$url];
        }

        foreach ($url as $u) {
            if (filter_var($u, FILTER_VALIDATE_URL) === false) {
                return ['error' => 'Invalid URL'];
            }
        }

        $response = self::fileDownloadFopen($url[0], $saveFile);
        if ($response['status'] !== 'success') {
            $response = self::fileDownloadCurl($url[0], $saveFile);
        }

        if ($response['status'] === 'success') {
            self::checkGzip($saveFile);
        }

        return $response;
    }

    private static function fileDownloadFopen($url, $fileName, $headerSize = 0) {
        $context = stream_context_create([
            'http' => [
                'method' => 'GET',
                'header' => "Connection: close\r\n",
                'timeout' => 10,
            ],
        ]);

        $handle = fopen($url, 'rb', false, $context);
        if (!$handle) {
            return ['status' => 'error', 'message' => 'Failed to open URL'];
        }

        $file = fopen($fileName, 'wb');
        if (!$file) {
            fclose($handle);
            return ['status' => 'error', 'message' => 'Failed to create local file'];
