            $url = [$url];
        }

        foreach ($url as $u) {
            if (filter_var($u, FILTER_VALIDATE_URL) === false) {
                return ['error' => 'Invalid URL'];
            }
        }

        $fileInfo = pathinfo($saveFile);
        if (!is_dir($fileInfo['dirname'])) {
            mkdir($fileInfo['dirname'], 0777, true);
        }

        $tempFile = $saveFile . '.tmp';
        $existsLength = file_exists($tempFile) ? filesize($tempFile) : 0;

        $headers = get_headers($url[0], 1);
        $supportRange = isset($headers['Accept-Ranges']) && $headers['Accept-Ranges'] === 'bytes';

        if ($supportRange && $existsLength > 0) {
            return self::fileDownloadCurl($url[0], $tempFile, true, $existsLength, $headers['Content-Length']);
        } else {
            return self::fileDownloadFopen($url[0], $tempFile);
        }
    }

    private static function fileDownloadFopen($url, $fileName, $headerSize = 0) {
        $context = stream_context_create([
            'http' => [
                'method' => 'GET',
                'header' => "Connection: close\r\n",
                'timeout' => 10,
            ],
        ]);

        $handle = fopen($url, 'rb', false, $context);
        if (!$handle) {
            return ['error' => 'Failed to open URL'];
        }

        $fp = fopen($fileName, 'wb');
        if (!$fp) {
            fclose($handle);
            return ['error' => 'Failed to open save file'];
        }

        while (!feof($handle)) {
            fwrite($fp, fread($handle, 8192));
        }

        fclose($fp);
        fclose($handle);

        rename($fileName, str_replace('.tmp', '', $fileName));
        return ['success' => true];
    }

    private static function fileDownloadCurl($url, $fileName, $supportRange = false, $existsLength = 0, $length = 0) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, 10);
        curl_setopt($ch, CURLOPT_HEADER, true);

        if ($supportRange && $existsLength > 0) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, [
                "Range: bytes=$existsLength-$length"
            ]);
            $fp = fopen($fileName, 'ab');
        } else {
            $fp = fopen($fileName, 'wb');
        }

        if (!$fp) {
            curl_close($ch);
            return ['error' => 'Failed to open save file'];
        }

        curl_setopt($ch, CURLOPT_WRITEFUNCTION, function($ch, $data) use ($fp) {
            fwrite($fp, $data);
            return strlen($data);
        });

        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        fclose($fp);

        if ($httpCode == 200 || $httpCode == 206) {
            rename($fileName, str_replace('.tmp', '', $fileName));
            return ['success' => true];
        } else {
            return ['error' => 'Download failed with HTTP code ' . $httpCode];
        }
    }

    private static function checkGzip($file) {
        if (substr($file, -3) === '.gz') {
            $decompressedFile = substr($file, 0, -3);
            $gz = gzopen($file, 'rb');
            $fp = fopen($decompressedFile, 'wb');

            while (!gzeof($gz)) {
                fwrite($fp, gzread($gz, 4096));
            }

            fclose($fp);
            gzclose($gz);
            unlink($file);
        }
    }
