class Downloader {
    public static function start($url, $saveFile, $timeout = 10) {
        if (!is_array($url)) {
            $url = [$url];
        }

        foreach ($url as $u) {
            if (filter_var($u, FILTER_VALIDATE_URL) === false) {
                return ['error' => 'Invalid URL'];
            }
        }

        $response = [];
        foreach ($url as $u) {
            $response[] = self::download($u, $saveFile, $timeout);
        }

        return $response;
    }

    private static function download($url, $saveFile, $timeout) {
        $headers = get_headers($url, 1);
        $supportRange = isset($headers['Accept-Ranges']) && $headers['Accept-Ranges'] === 'bytes';
        $contentLength = isset($headers['Content-Length']) ? (int)$headers['Content-Length'] : 0;

        if (function_exists('curl_init') && $supportRange) {
            return self::fileDownloadCurl($url, $saveFile, $supportRange, filesize($saveFile ?? ''), $contentLength);
        } else {
            return self::fileDownloadFopen($url, $saveFile);
        }
    }

    private static function fileDownloadFopen($url, $fileName, $headerSize = 0) {
        $context = stream_context_create([
            'http' => [
                'method' => 'GET',
                'header' => "Range: bytes=$headerSize-\r\n",
                'timeout' => 10,
            ],
        ]);

        $handle = fopen($url, 'rb', false, $context);
        if (!$handle) {
            return ['error' => 'Failed to open stream'];
        }

        $file = fopen($fileName, 'ab');
        if (!$file) {
            fclose($handle);
            return ['error' => 'Failed to open file for writing'];
        }

        while (!feof($handle)) {
            fwrite($file, fread($handle, 8192));
        }

        fclose($handle);
        fclose($file);

        self::checkGzip($fileName);

