        // Save the image data to a file
        $file = fopen($filepath, 'wb');
        if ($file) {
            fwrite($file, $imageData);
            fclose($file);
            echo "Image downloaded and saved successfully.";
        } else {
            echo "Failed to open file for writing.";
        }
    }

    // Close the cURL session
    curl_close($ch);
}
