class Timthumb {
    public $curlFH = false;
    public $curlDataWritten = 0;
    public $lastURLError = false;

    public static function curlWrite($h, $d) {
        $instance = self::getInstance();
        if ($instance->curlFH === false) {
            return 0;
        }
        $bytesWritten = fwrite($instance->curlFH, $d);
        if ($bytesWritten === false) {
            return 0;
        }
        $instance->curlDataWritten += $bytesWritten;
        // Example size limit of 1MB
        if ($instance->curlDataWritten > 1048576) {
            fclose($instance->curlFH);
            $instance->curlFH = false;
            return 0;
        }
        return $bytesWritten;
    }

    public function getURL($url, $tempfile) {
        if (function_exists('curl_init')) {
            $ch = curl_init($url);
            $this->curlFH = fopen($tempfile, 'wb');
            if ($this->curlFH === false) {
                $this->lastURLError = "Failed to open temporary file.";
                return false;
            }
            curl_setopt($ch, CURLOPT_WRITEFUNCTION, array(__CLASS__, 'curlWrite'));
            curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
            curl_setopt($ch, CURLOPT_MAXREDIRS, 10);
            curl_setopt($ch, CURLOPT_TIMEOUT, 30);
            $result = curl_exec($ch);
            if ($result === false) {
                $this->lastURLError = curl_error($ch);
                curl_close($ch);
                fclose($this->curlFH);
                unlink($tempfile);
                return false;
            }
            $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
            curl_close($ch);
            fclose($this->curlFH);
            if ($httpCode != 200) {
                unlink($tempfile);
                return false;
            }
            return true;
        } else {
            $content = @file_get_contents($url);
            if ($content === false) {
                $this->lastURLError = "Failed to fetch content using file_get_contents.";
                return false;
            }
            if (strlen($content) > 1048576) {
                return false;
            }
            file_put_contents($tempfile, $content);
            return true;
        }
    }

    private static function getInstance() {
        return new self();
    }
}
