        curl_setopt($ch, CURLOPT_WRITEFUNCTION, [$this, 'writeCallback']);

        if ($headers !== null) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        }

        if ($useragent !== null) {
            curl_setopt($ch, CURLOPT_USERAGENT, $useragent);
