            $location = $this->getHeader('Location');
            if ($location !== null) {
                $this->makeRequest($location, $timeout, $redirects - 1, $headers, $useragent, $force_fsockopen, $curl_options);
            }
        } else {
            $this->success = $this->status_code === 200;
