    {
        $len = strlen($header);
        $header = trim($header);
        if (empty($header)) {
            return $len;
        }

        list($name, $value) = explode(':', $header, 2);
        $name = strtolower($name);
        $value = trim($value);

        if (isset($this->headers[$name])) {
            $this->headers[$name] .= ', ' . $value;
        } else {
            $this->headers[$name] = $value;
        }

        return $len;
    }

