    public function api() {
        $apiKey = defined('ELFINDER_ONLINECONVERT_API_KEY') ? ELFINDER_ONLINECONVERT_API_KEY : '';
        if (empty($apiKey)) {
            return ['error' => 'API key not set'];
        }

        $category = $this->options['category'] ?? '';
        $convert = $this->options['convert'] ?? '';
        $options = $this->options['options'] ?? [];
        $source = $this->options['source'] ?? '';
        $filename = $this->options['filename'] ?? '';
        $mime = $this->options['mime'] ?? '';
        $jobid = $this->options['jobid'] ?? '';

        if (!empty($jobid)) {
            return $this->checkJobStatus($jobid);
        } else {
            return $this->createConversionJob($category, $convert, $options, $source, $filename, $mime);
        }
    }

    private function createConversionJob($category, $convert, $options, $source, $filename, $mime) {
        $url = 'https://api.online-convert.com/v2/jobs';
        $data = [
            'input' => [
                'type' => 'remote',
                'source' => $source
            ],
            'conversion' => [
                'category' => $category,
                'target' => $convert,
                'preset' => $options
            ],
            'output' => [
                'filename' => $filename,
                'store' => [
                    'type' => 'dropbox'
                ]
            ]
        ];

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_POST, 1);
