function makeHttpRequest($url, $auth = true, $customRequest = 'GET', $postFields = []) {
    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $url);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);

    if ($auth) {
        // Example of setting up basic authentication
        curl_setopt($ch, CURLOPT_USERPWD, "username:password");
    }

    if ($customRequest !== 'GET') {
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $customRequest);
    }

    if (!empty($postFields)) {
        curl_setopt($ch, CURLOPT_POSTFIELDS, json_encode($postFields));
        curl_setopt($ch, CURLOPT_HTTPHEADER, [
            'Content-Type: application/json',
            'Content-Length: ' . strlen(json_encode($postFields))
        ]);
    }

    // Proxy configuration example
    // curl_setopt($ch, CURLOPT_PROXY, "proxy.example.com:8080");

    $response = curl_exec($ch);
    $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);

    if ($httpCode >= 400) {
        // Handle errors
        return null;
    }

    curl_close($ch);

    $decodedResponse = json_decode($response, true);
    return $decodedResponse ? $decodedResponse : null;
}
