    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true); // Follow redirects

    // Execute cURL session
    $response = curl_exec($ch);

    // Check for cURL errors
    if(curl_errno($ch)) {
        return false;
    }

    // Close cURL session
    curl_close($ch);

    // Return the response
    return $response;
}
