function serveFavicon($url, $cache_time, $default_ico, $basename = "favicon.ico") {
    $cache_dir = __DIR__ . '/cache/';
    $cache_file = $cache_dir . md5($url) . '.ico';
    $current_time = time();

    // Check if cache exists and is still valid
    if (file_exists($cache_file) && ($current_time - filemtime($cache_file) < $cache_time)) {
        header('Content-Type: image/x-icon');
        readfile($cache_file);
        return;
    }

    // Prepare cURL options
    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, rtrim($url, '/') . '/' . $basename);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    curl_setopt($ch, CURLOPT_TIMEOUT, 10); // Timeout after 10 seconds

    // Execute cURL request
    $response = curl_exec($ch);
    $http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);

    // Close cURL session
    curl_close($ch);

    // Validate response
    if ($http_code == 200 && @getimagesizefromstring($response)) {
        // Save to cache
        if (!is_dir($cache_dir)) {
            mkdir($cache_dir, 0777, true);
        }
        file_put_contents($cache_file, $response);

        // Serve the cached favicon
