    public function getResponse() {
        if ($this->use_curl) {
            $this->incoming_payload = curl_exec($this->curl_handle);
            $this->incoming_headers = curl_getinfo($this->curl_handle);
            if (curl_errno($this->curl_handle)) {
                return false;
            }
            curl_close($this->curl_handle);
            return $this->incoming_payload;
        } else {
            $this->incoming_payload = stream_get_contents($this->connection);
            fclose($this->connection);
            list($headers, $body) = preg_split("/\r?\n\r?\n/", $this->incoming_payload, 2);
            $this->incoming_headers = $this->parseHeaders($headers);
            $this->incoming_payload = $body;
            return $this->incoming_payload;
        }
    }

    public function setHeader($name, $value) {
        $this->outgoing_headers[$name] = $value;
    }

    public function setCurlOption($option, $value) {
        if ($this->use_curl) {
            curl_setopt($this->curl_handle, $option, $value);
        }
    }

    private function parseHeaders($header_string) {
        $headers = [];
        $lines = explode("\r\n", trim($header_string));
        foreach ($lines as $line) {
            if (strpos($line, ':') !== false) {
                list($name, $value) = explode(':', $line, 2);
                $headers[trim($name)] = trim($value);
            }
        }
        return $headers;
    }

    public function parseCookie($cookie_str) {
        $cookies = [];
        $cookie_parts = explode(';', $cookie_str);
        foreach ($cookie_parts as $part) {
            $part = trim($part);
            if (strpos($part, '=') !== false) {
                list($name, $value) = explode('=', $part, 2);
                $cookies[trim($name)] = trim($value);
            }
        }
        return empty($cookies) ? false : $cookies;
    }
}

