        $this->SetUsername();
        $this->SetPassword();
        $this->SetNasIpAddress();
        $this->SetNasPort();

        $this->ClearLastError();
        $this->ClearDataToSend();
        $this->ClearDataReceived();
    }

    function AccessRequest($username = '', $password = '', $udp_timeout = 0, $state = NULL)
    {
        $this->ClearDataReceived();
        $this->ClearLastError();

        $this->SetPacketCodeToSend(1);

        if (0 < strlen($username))
        {
            $this->SetUsername($username);
        }

        if (0 < strlen($password))
        {
            $this->SetPassword($password);
        }

        if ($state !== NULL)
        {
            $this->SetAttribute(24, $state);
        }
        else
        {
            $this->SetAttribute(6, 1);
        }

        if (intval($udp_timeout) > 0)
        {
            $this->SetUdpTimeout($udp_timeout);
        }

        $attributes_content = '';
        $attribute_count1 = count((array)$this->_attributes_to_send);
        for ($attributes_loop = 0; $attributes_loop < $attribute_count1; $attributes_loop++)
        {
            $attributes_content .= $this->_attributes_to_send[$attributes_loop];
        }

        $packet_length  = 4;
        $packet_length += strlen($this->_request_authenticator);
        $packet_length += strlen($attributes_content);

        $packet_data  = chr($this->_radius_packet_to_send);
        $packet_data .= chr($this->GetNextIdentifier());
        $packet_data .= chr(intval($packet_length / 256));
        $packet_data .= chr(intval($packet_length % 256));
        $packet_data .= $this->_request_authenticator;
        $packet_data .= $attributes_content;

        $_socket_to_server = socket_create(AF_INET, SOCK_DGRAM, 17);

        if ($_socket_to_server === FALSE)
        {
            $this->_last_error_code    = socket_last_error();
            $this->_last_error_message = socket_strerror($this->_last_error_code);
        }
        elseif (FALSE === socket_connect($_socket_to_server, $this->_ip_radius_server, $this->_authentication_port))
        {
            $this->_last_error_code    = socket_last_error();
            $this->_last_error_message = socket_strerror($this->_last_error_code);
        }
        elseif (FALSE === socket_write($_socket_to_server, $packet_data, $packet_length))
        {
            $this->_last_error_code    = socket_last_error();
            $this->_last_error_message = socket_strerror($this->_last_error_code);
        }
        else
        {
            $read_socket_array   = array($_socket_to_server);
            $write_socket_array  = NULL;
            $except_socket_array = NULL;

            $received_packet = chr(0);

            if (!(FALSE === socket_select($read_socket_array, $write_socket_array, $except_socket_array, $this->_udp_timeout)))
            {
                if (in_array($_socket_to_server, $read_socket_array))
                {
                    if (FALSE === ($received_packet = @socket_read($_socket_to_server, 1024)))
                    {
                        $received_packet = chr(0);
                        $this->_last_error_code    = socket_last_error();
                        $this->_last_error_message = socket_strerror($this->_last_error_code);
                    }
                    else
                    {
                        socket_close($_socket_to_server);
                    }
                }
            }
            else
            {
                socket_close($_socket_to_server);
            }
        }

        $this->_radius_packet_received = intval(ord(substr($received_packet, 0, 1)));

        if ($this->_radius_packet_received > 0)
        {
            $this->_identifier_received = intval(ord(substr($received_packet, 1, 1)));
            $packet_length = (intval(ord(substr($received_packet, 2, 1))) * 256) + (intval(ord(substr($received_packet, 3, 1)));
            $this->_response_authenticator = substr($received_packet, 4, 16);
            $attributes_content = substr($received_packet, 20, ($packet_length - 4 - 16));
            while (strlen($attributes_content) > 2)
            {
                $attribute_type = intval(ord(substr($attributes_content, 0, 1)));
                $attribute_length = intval(ord(substr($attributes_content, 1, 1)));
                $attribute_raw_value = substr($attributes_content, 2, $attribute_length - 2);
                $attributes_content = substr($attributes_content, $attribute_length);

                $attribute_value = $this->DecodeAttribute($attribute_raw_value, $attribute_type);

                $this->_attributes_received[] = array($attribute_type, $attribute_value);
            }
        }

        return (2 == ($this->_radius_packet_received));
    }
}

namespace common\modules\torrent_scraper\models;

use common\models\ScraperException;

class UdpScraper extends BaseScraper
{
    const ACTION_CONNECT = 0;
    const ACTION_ANNOUNCE = 1;
    const ACTION_SCRAPE = 2;
    const ACTION_ERROR = 3;

    const CONNECTION_ID = "\x00\x00\x04\x17\x27\x10\x19\x80";

    protected $currentConnectionId = null;
    protected $transactionId = null;
    protected $socket = null;
    protected $timeout = 15;

    public function scrape(Tracker $tracker, array $hashes)
    {
        $chunks = array_chunk($hashes, 74);
        $result = [];
        foreach ($chunks as $chunk) {
            $result = array_merge($result, $this->scrapeChunk($tracker, $chunk));
        }
        return $result;
    }

    public function scrapeChunk(Tracker $tracker, $hashes)
    {
        $this->initSocket();
        $this->connect($tracker->host, $tracker->port);
        $this->startTransaction();
        $torrents = $this->scrapeHashes($hashes);
        $this->closeSocket();
        return $torrents;
    }

    protected function initSocket()
    {
        if ($this->socket === null) {
            $socket = socket_create(AF_INET, SOCK_DGRAM, SOL_UDP);
            if ($socket === false) {
                throw new UdpScraperException(socket_strerror(socket_last_error()));
            }
            $this->socket = $socket;
        }
        return $this->socket;
    }

    protected function connect($domain, $port)
    {
        if (socket_connect($this->socket, $domain, $port) === false) {
            throw new UdpScraperException(socket_strerror(socket_last_error($this->socket)));
        }
    }

    protected function generateTransactionId()
    {
        $this->transactionId = mt_rand(0, 65535);
    }

    protected function startTransaction()
    {
        $this->generateTransactionId();
        $packet = static::CONNECTION_ID . pack('N', static::ACTION_CONNECT) . pack('N', $this->transactionId);
        $res = $this->request($packet, 16);
        if (strlen($res) < 1) {
            throw new UdpScraperException('No connection response.');
        }
        if (strlen($res) < 16) {
            throw new UdpScraperException('Too short connection response.');
        }
        $resUnpacked = unpack("Naction/Ntransid", $res);

        if ($resUnpacked['action'] != static::ACTION_CONNECT || $resUnpacked['transid'] != $this->transactionId) {
            throw new UdpScraperException('Invalid connection response.');
        }

        $this->currentConnectionId = substr($res, 8, 8);
    }

    protected function scrapeHashes($hashes)
    {
        $hashString = '';
        foreach ($hashes as $hash) {
            $hashString .= pack('H*', $hash);
        }

        $packet = $this->currentConnectionId . pack("N", static::ACTION_SCRAPE) . pack("N", $this->transactionId) . $hashString;

        $readLength = 8 + (12 * count($hashes));

        $res = $this->request($packet, $readLength);

        if (strlen($res) < 1) {
            throw new UdpScraperException('No scrape response.');
        }
        if (strlen($res) < 8) {
            throw new UdpScraperException('Too short scrape response.');
        }

        $resUnpacked = unpack("Naction/Ntransid", $res);

        if ($resUnpacked['action'] == static::ACTION_ERROR) {
            $this->checkError($res);
        }

        if (strlen($res) < $readLength) {
            throw new UdpScraperException('Too short scrape response.');
        }

        $torrents = [];
        $index = 8;
        foreach ($hashes as $hash) {
            $resUnpacked = unpack("Ncomplete/Ndownloaded/Nincomplete", substr($res, $index, 12));
            $torrents[$hash] = $resUnpacked;
            $index = $index + 12;
        }
        return $torrents;
    }

    protected function request($packet, $readLength)
    {
        socket_set_option($this->socket, SOL_SOCKET, SO_RCVTIMEO, ['sec' => $this->timeout, 'usec' => 0]);
        socket_set_option($this->socket, SOL_SOCKET, SO_SNDTIMEO, ['sec' => $this->timeout, 'usec' => 0]);

        if (socket_write($this->socket, $packet, strlen($packet)) === false) {
            throw new UdpScraperException(socket_strerror(socket_last_error($this->socket)));
        }

        $res = socket_read($this->socket, $readLength, PHP_BINARY_READ);
        return $res;
    }

    protected function closeSocket()
    {
        if ($this->socket) {
            socket_close($this->socket);
            $this->socket = null;
            $this->transactionId = null;
            $this->currentConnectionId = null;
        }
    }

    protected function checkError($response)
    {
        $unpacked = unpack("Naction/Ntransid/H*error", $response);
        throw new UdpScraperException($unpacked['error']);
    }
}

