        $host = parse_url($url, PHP_URL_HOST);
        $path = parse_url($url, PHP_URL_PATH);

        $fp = fsockopen($host, 80, $errno, $errstr, 30);
        if (!$fp) {
            $this->set_error("Failed to connect: $errstr ($errno)");
            return FALSE;
        }

        $out = "POST $path HTTP/1.1\r\n";
        $out .= "Host: $host\r\n";
        $out .= "Content-Type: application/x-www-form-urlencoded\r\n";
        $out .= "Content-Length: " . strlen(http_build_query($this->data)) . "\r\n";
        $out .= "Connection: Close\r\n\r\n";
        $out .= http_build_query($this->data);

        fwrite($fp, $out);
        while (!feof($fp)) {
            $this->response .= fgets($fp, 128);
        }
        fclose($fp);

        if (strpos($this->response, 'HTTP/1.1 200 OK') === false) {
            $this->set_error('Trackback request failed');
            return FALSE;
        }

        return TRUE;
    }

    public function get_id($url) {
        preg_match('/\/(\d+)\//', $url, $matches);
        return isset($matches[1]) ? $matches[1] : FALSE;
    }

    public function set_error($msg) {
        $this->error_msg[] = $msg;
    }
