        $this->host = isset($parsed_url['host']) ? $parsed_url['host'] : '';
        $this->port = isset($parsed_url['port']) ? $parsed_url['port'] : ($this->scheme === 'https' ? '443' : '80');
        $this->path = isset($parsed_url['path']) ? $parsed_url['path'] : '/';
        $this->use_curl = $use_curl;
        if ($this->use_curl && $curl_options !== null) {
            curl_setopt_array($this->ch, $curl_options);
        }
    }

    public function connect($connection_timeout = 0, $response_timeout = 30) {
        if ($this->use_curl) {
            $this->ch = curl_init();
            curl_setopt($this->ch, CURLOPT_URL, $this->url);
            curl_setopt($this->ch, CURLOPT_RETURNTRANSFER, true);
            curl_setopt($this->ch, CURLOPT_TIMEOUT, $response_timeout);
            curl_setopt($this->ch, CURLOPT_CONNECTTIMEOUT, $connection_timeout);
            if ($this->proxy !== null) {
                curl_setopt($this->ch, CURLOPT_PROXY, $this->proxy['host']);
                curl_setopt($this->ch, CURLOPT_PROXYPORT, $this->proxy['port']);
                if (isset($this->proxy['auth'])) {
                    curl_setopt($this->ch, CURLOPT_PROXYUSERPWD, $this->proxy['auth']);
                }
            }
            return true;
        } else {
            $socket = @fsockopen($this->host, $this->port, $errno, $errstr, $connection_timeout);
            if (!$socket) {
                return false;
            }
            stream_set_timeout($socket, $response_timeout);
            if ($this->persistentConnection) {
                fwrite($socket, "Host: {$this->host}\r\n");
                fwrite($socket, "Connection: Keep-Alive\r\n");
            }
            foreach ($this->outgoing_headers as $header) {
                fwrite($socket, $header . "\r\n");
            }
            fwrite($socket, "\r\n");
            return $socket;
        }
    }

