        if ($this->use_curl && $curl_options !== null) {
            curl_setopt_array($this->getCurlHandle(), $curl_options);
        }
    }

    private function parseUrl() {
        $parsed_url = parse_url($this->url);
        $this->scheme = isset($parsed_url['scheme']) ? $parsed_url['scheme'] : 'http';
        $this->host = isset($parsed_url['host']) ? $parsed_url['host'] : '';
        $this->port = isset($parsed_url['port']) ? $parsed_url['port'] : ($this->scheme === 'https' ? '443' : '80');
        $this->path = isset($parsed_url['path']) ? $parsed_url['path'] : '/';
        $this->query = isset($parsed_url['query']) ? '?' . $parsed_url['query'] : '';
        $this->fragment = isset($parsed_url['fragment']) ? '#' . $parsed_url['fragment'] : '';
    }

    public function connect($connection_timeout = 0, $response_timeout = 30) {
        if ($this->use_curl) {
            return curl_exec($this->getCurlHandle());
        } else {
            $socket = @fsockopen($this->host, $this->port, $errno, $errstr, $connection_timeout);
            if (!$socket) {
                throw new Exception("Failed to connect: $errstr ($errno)");
            }
            stream_set_timeout($socket, $response_timeout);
            return true;
        }
    }

    public function send($data, $timeout = 0, $response_timeout = 30, $cookies = null) {
        $this->outgoing_payload = $data;
        $this->sendRequest($data, $cookies);
        return $this->getResponse();
    }

    private function sendRequest($data, $cookies = null) {
        $headers = [
            "Host: {$this->host}",
            "Content-Type: application/x-www-form-urlencoded",
            "Content-Length: " . strlen($data),
            "Connection: " . ($this->persistentConnection ? 'Keep-Alive' : 'Close')
        ];
        if ($cookies !== null) {
            $headers[] = "Cookie: " . implode('; ', $cookies);
        }
        foreach ($this->outgoing_headers as $header) {
            $headers[] = $header;
        }
        $request = "$this->request_method {$this->path} HTTP/{$this->protocol_version}\r\n" . implode("\r\n", $headers) . "\r\n\r\n$data";
        if ($this->use_curl) {
            curl_setopt($this->getCurlHandle(), CURLOPT_POSTFIELDS, $request);
            curl_setopt($this->getCurlHandle(), CURLOPT_HTTPHEADER, $headers);
        } else {
            fwrite($this->getCurlHandle(), $request);
        }
    }

