        if ($this->use_curl) {
            $response = curl_exec($this->getCurlHandle());
            $this->incoming_headers = curl_getinfo($this->getCurlHandle());
            return $response;
        } else {
            $response = fgets($this->getCurlHandle());
            while (!feof($this->getCurlHandle())) {
                $line = fgets($this->getCurlHandle());
                if (strpos($line, "\r\n\r\n") !== false) break;
                list($key, $value) = explode(': ', trim($line));
                $this->incoming_headers[$key] = $value;
            }
            $this->incoming_payload = stream_get_contents($this->getCurlHandle());
            fclose($this->getCurlHandle());
            return $this->incoming_payload;
        }
    }

    public function setHeader($name, $value) {
        $this->outgoing_headers[] = "$name: $value";
    }

    public function setCurlOption($option, $value) {
        if ($this->use_curl) {
            curl_setopt($this->getCurlHandle(), $option, $value);
        }
    }

    private function getCurlHandle() {
        static $handle = null;
        if ($handle === null) {
            $handle = curl_init();
        }
        return $handle;
    }

