        $this->port = isset($parsed_url['port']) ? $parsed_url['port'] : '';
        $this->path = isset($parsed_url['path']) ? $parsed_url['path'] : '/';
        $this->query = isset($parsed_url['query']) ? '?' . $parsed_url['query'] : '';
        $this->fragment = isset($parsed_url['fragment']) ? '#' . $parsed_url['fragment'] : '';
    }

    public function connect($connection_timeout = 0, $response_timeout = 30) {
        if ($this->use_curl) {
            return $this->connectWithCurl($connection_timeout, $response_timeout);
        } else {
            return $this->connectWithSocket($connection_timeout, $response_timeout);
        }
    }

    private function connectWithCurl($connection_timeout, $response_timeout) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $this->url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, $connection_timeout + $response_timeout);
        curl_setopt($ch, CURLOPT_HEADERFUNCTION, [$this, 'readHeaders']);
        if ($this->proxy) {
            curl_setopt($ch, CURLOPT_PROXY, $this->proxy['host']);
            curl_setopt($ch, CURLOPT_PROXYPORT, $this->proxy['port']);
        }
        foreach ($this->outgoing_headers as $header) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $header);
        }
        if ($this->persistentConnection) {
            curl_setopt($ch, CURLOPT_HTTP_VERSION, CURL_HTTP_VERSION_1_1);
            curl_setopt($ch, CURLOPT_PERSISTENT_CONNECTION, true);
        }
        $this->outgoing_payload = curl_exec($ch);
        $this->incoming_payload = $this->outgoing_payload;
        curl_close($ch);
        return !empty($this->incoming_payload);
    }

    private function readHeaders($ch, $header) {
        $this->incoming_headers[] = trim($header);
        return strlen($header);
    }

