class soap_transport_http {
    private $url;
    private $uri;
    private $scheme;
    private $host;
    private $port;
    private $path;
    private $request_method = 'POST';
    private $protocol_version = '1.0';
    private $outgoing_headers = [];
    private $incoming_headers = [];
    private $outgoing_payload;
    private $incoming_payload;
    private $persistentConnection = false;
    private $use_curl = false;
    private $proxy;

    public function __construct($url, $curl_options = null, $use_curl = false) {
        $this->url = $url;
        $this->parseUrl();
        $this->use_curl = $use_curl;
        if ($this->use_curl && $curl_options !== null) {
            $this->setCurlOptions($curl_options);
        }
    }

    private function parseUrl() {
        $parsed_url = parse_url($this->url);
        $this->scheme = isset($parsed_url['scheme']) ? $parsed_url['scheme'] : '';
        $this->host = isset($parsed_url['host']) ? $parsed_url['host'] : '';
        $this->port = isset($parsed_url['port']) ? $parsed_url['port'] : '';
        $this->path = isset($parsed_url['path']) ? $parsed_url['path'] : '/';
        $this->query = isset($parsed_url['query']) ? '?' . $parsed_url['query'] : '';
        $this->fragment = isset($parsed_url['fragment']) ? '#' . $parsed_url['fragment'] : '';
        $this->uri = $this->path . $this->query . $this->fragment;
    }

    public function connect($connection_timeout = 0, $response_timeout = 30) {
        if ($this->use_curl) {
            return $this->connectWithCurl($connection_timeout, $response_timeout);
        } else {
            return $this->connectWithSocket($connection_timeout, $response_timeout);
        }
    }

    private function connectWithCurl($connection_timeout, $response_timeout) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $this->url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, $response_timeout);
        curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, $connection_timeout);
        if ($this->proxy !== null) {
            curl_setopt($ch, CURLOPT_PROXY, $this->proxy['host']);
            curl_setopt($ch, CURLOPT_PROXYPORT, $this->proxy['port']);
        }
        if ($this->persistentConnection) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, ['Connection: keep-alive']);
        }
        foreach ($this->outgoing_headers as $header) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $header);
        }
        return curl_exec($ch);
    }

    private function connectWithSocket($connection_timeout, $response_timeout) {
        $socket = fsockopen($this->host, $this->port, $errno, $errstr, $connection_timeout);
        if (!$socket) {
            return false;
        }
        stream_set_timeout($socket, $response_timeout);
        return $socket;
    }

    public function send($data, $timeout = 0, $response_timeout = 30, $cookies = null) {
        $this->outgoing_payload = $data;
        if ($this->sendRequest($data, $cookies)) {
            return $this->getResponse();
        }
        return false;
    }

    private function sendRequest($data, $cookies) {
        $headers = [
            "Host: {$this->host}",
            "Content-Type: application/xml",
            "Content-Length: " . strlen($data),
            "User-Agent: PHP Soap Client"
        ];
        if ($cookies !== null) {
            $headers[] = "Cookie: " . implode('; ', $cookies);
        }
        foreach ($this->outgoing_headers as $header) {
            $headers[] = $header;
        }
        $request = implode("\r\n", $headers) . "\r\n\r\n" . $data;
        if ($this->use_curl) {
            return $this->sendRequestWithCurl($request);
        } else {
            return $this->sendRequestWithSocket($request);
        }
    }

    private function sendRequestWithCurl($request) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $this->url);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $request);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, true);
        if ($this->proxy !== null) {
            curl_setopt($ch, CURLOPT_PROXY, $this->proxy['host']);
            curl_setopt($ch, CURLOPT_PROXYPORT, $this->proxy['port']);
        }
        if ($this->persistentConnection) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, ['Connection: keep-alive']);
        }
        $response = curl_exec($ch);
        if ($response === false) {
            return false;
        }
        list($this->incoming_headers, $this->incoming_payload) = explode("\r\n\r\n", $response, 2);
        return true;
    }

    private function sendRequestWithSocket($request) {
        $socket = $this->connect();
        if (!$socket) {
            return false;
        }
        fwrite($socket, $request);
        $response = fread($socket, 8192);
        fclose($socket);
        list($this->incoming_headers, $this->incoming_payload) = explode("\r\n\r\n", $response, 2);
        return true;
    }

    public function getResponse() {
        return $this->incoming_payload;
    }

    public function setHeader($name, $value) {
        $this->outgoing_headers[$name] = "$name: $value";
    }

    public function setCurlOption($option, $value) {
        if ($this->use_curl) {
            curl_setopt($this, $option, $value);
        }
    }

    public function parseCookie($cookie_str) {
        $cookies = [];
        $parts = explode(';', $cookie_str);
        foreach ($parts as $part) {
            list($key, $value) = explode('=', trim($part));
            $cookies[trim($key)] = trim($value);
        }
        return $cookies;
    }
}
