<?php
class Http {
    public static function fsockopenDownload($url, $conf = []) {
        $defaults = [
            'limit' => 0,
            'post' => null,
            'cookie' => '',
            'ip' => '',
            'timeout' => 30,
            'block' => true
        ];
        $conf = array_merge($defaults, $conf);

        $parsedUrl = parse_url($url);
        if (!isset($parsedUrl['scheme']) || !isset($parsedUrl['host'])) {
            return '';
        }

        $scheme = $parsedUrl['scheme'];
        $host = $parsedUrl['host'];
        $port = isset($parsedUrl['port']) ? $parsedUrl['port'] : ($scheme === 'https' ? 443 : 80);
        $path = isset($parsedUrl['path']) ? $parsedUrl['path'] : '/';
        $query = isset($parsedUrl['query']) ? '?' . $parsedUrl['query'] : '';

        $headers = [];
        $method = isset($conf['post']) ? 'POST' : 'GET';
        $body = '';

        if ($method === 'POST') {
            $contentLength = strlen(http_build_query($conf['post']));
            $headers[] = "Content-Type: application/x-www-form-urlencoded";
            $headers[] = "Content-Length: $contentLength";
            $body = http_build_query($conf['post']);
        } else {
            $headers[] = "Accept: */*";
        }

        if (!empty($conf['cookie'])) {
            $headers[] = "Cookie: " . $conf['cookie'];
        }

        $socket = @fsockopen(($conf['ip'] !== '' ? $conf['ip'] : $host), $port, $errno, $errstr, $conf['timeout']);
        if (!$socket) {
            return '';
        }

        stream_set_blocking($socket, $conf['block']);

        $request = "$method $path$query HTTP/1.1\r\nHost: $host\r\n" . implode("\r\n", $headers) . "\r\n\r\n$body";
        fwrite($socket, $request);

        $response = '';
        while (!feof($socket)) {
            $response .= fgets($socket, $conf['limit']);
        }

        fclose($socket);

        preg_match('/\r\n\r\n(.*)/', $response, $matches);
        return isset($matches[1]) ? $matches[1] : '';
    }
}
?>
