        $parsedUrl = parse_url($url);
        if (!isset($parsedUrl['scheme'], $parsedUrl['host'])) {
            return '';
        }

        $scheme = $parsedUrl['scheme'];
        $host = $parsedUrl['host'];
        $port = isset($parsedUrl['port']) ? $parsedUrl['port'] : ($scheme === 'https' ? 443 : 80);
        $path = isset($parsedUrl['path']) ? $parsedUrl['path'] : '/';
        $query = isset($parsedUrl['query']) ? '?' . $parsedUrl['query'] : '';

        $headers = [];
        $headers[] = "Host: $host";
        $headers[] = "User-Agent: PHP-HttpClient";
        if ($conf['cookie']) {
            $headers[] = "Cookie: " . $conf['cookie'];
        }
        if ($conf['post']) {
            $body = is_array($conf['post']) ? http_build_query($conf['post']) : $conf['post'];
            $headers[] = "Content-Type: application/x-www-form-urlencoded";
            $headers[] = "Content-Length: " . strlen($body);
        } else {
            $headers[] = "Accept: */*";
        }

        $socket = @fsockopen(($conf['ip'] ? "$conf[ip]" : $host), $port, $errno, $errstr, $conf['timeout']);
        if (!$socket) {
            return '';
        }

        $request = ($conf['post'] ? "POST" : "GET") . " $path$query HTTP/1.1\r\n";
        foreach ($headers as $header) {
            $request .= "$header\r\n";
        }
        $request .= "\r\n";

