        if ($conf['post']) {
            fwrite($socket, $body);
        }

        $response = '';
        while (($line = fgets($socket)) !== false && ($conf['limit'] == 0 || strlen($response) < $conf['limit'])) {
            $response .= $line;
        }

        fclose($socket);
        return trim(substr($response, strpos($response, "\r\n\r\n") + 4));
    }
