use GuzzleHttp\Client;
use GuzzleHttp\Exception\RequestException;

class ImageX extends V4Curl {
    public function upload(string $uploadHost, array $storeInfo, string $filePath): int {
        if (!file_exists($filePath)) {
            return -1;
        }

        $client = new Client();
        $uri = $uploadHost . '/' . $storeInfo['StoreUri'];
        $headers = [
            'Authorization' => 'Bearer ' . $storeInfo['Auth'],
            'Content-Type' => mime_content_type($filePath),
            'Content-Length' => filesize($filePath),
            'X-CRC32' => dechex(crc32(file_get_contents($filePath)))
        ];

        try {
            $response = $client->put($uri, ['headers' => $headers, 'body' => fopen($filePath, 'r')]);
            if ($response->getStatusCode() == 200) {
                return 0;
            } else {
                return -2;
            }
        } catch (RequestException $e) {
            return -2;
        }
    }
}
?>
