            'limit' => 0,
            'post' => '',
            'cookie' => '',
            'ip' => '',
            'timeout' => 15,
            'block' => true
        );
        $conf = array_merge($defaults, $conf);

        $parts = parse_url($url);
        if (!isset($parts['host'])) return '';

        $host = isset($parts['port']) ? "$parts[host]:$parts[port]" : $parts['host'];
        $path = isset($parts['path']) ? $parts['path'] : '/';
        $query = isset($parts['query']) ? '?' . $parts['query'] : '';
        $protocol = isset($parts['scheme']) && $parts['scheme'] === 'https' ? 'ssl://' : 'tcp://';

        $fp = @fsockopen($protocol . $host, isset($parts['port']) ? $parts['port'] : null, $errno, $errstr, $conf['timeout']);
        if (!$fp) return '';

        $out = "GET $path$query HTTP/1.1\r\n";
        $out .= "Host: $host\r\n";
        $out .= "User-Agent: PHP\r\n";
        if ($conf['cookie']) $out .= "Cookie: " . $conf['cookie'] . "\r\n";
        $out .= "Connection: " . ($conf['block'] ? 'Keep-Alive' : 'close') . "\r\n";

        if (!empty($conf['post'])) {
