class Http {
    public static function fsockopenDownload($url, $conf = array()) {
        $defaults = array(
            'limit' => 0,
            'post' => '',
            'cookie' => '',
            'ip' => '',
            'timeout' => 15,
            'block' => true
        );
        $conf = array_merge($defaults, $conf);

        $parts = parse_url($url);
        if (!isset($parts['scheme'])) {
            return '';
        }
        $host = isset($parts['host']) ? $parts['host'] : '';
        $port = isset($parts['port']) ? $parts['port'] : ($parts['scheme'] == 'https' ? 443 : 80);
        $path = isset($parts['path']) ? $parts['path'] : '/';
        $query = isset($parts['query']) ? '?' . $parts['query'] : '';

        $fp = fsockopen(($conf['ip'] ? $conf['ip'] : $host), $port, $errno, $errstr, $conf['timeout']);
        if (!$fp) {
            return '';
        }

        $method = isset($conf['post']) ? 'POST' : 'GET';
        $headers = "Host: $host\r\n";
        $headers .= "User-Agent: PHP\r\n";
        $headers .= "Connection: " . ($conf['block'] ? 'Keep-Alive' : 'close') . "\r\n";
        if ($conf['cookie']) {
            $headers .= "Cookie: " . $conf['cookie'] . "\r\n";
        }
        if ($method == 'POST') {
            $contentLength = strlen(http_build_query($conf['post']));
            $headers .= "Content-Type: application/x-www-form-urlencoded\r\n";
            $headers .= "Content-Length: $contentLength\r\n";
        } else {
            $headers .= "Accept: */*\r\n";
        }
        $headers .= "\r\n";

        fwrite($fp, "$method $path$query HTTP/1.1\r\n$headers");
        if ($method == 'POST') {
            fwrite($fp, http_build_query($conf['post']));
        }

        $response = '';
        while (!feof($fp)) {
            $data = fgets($fp, $conf['limit'] > 0 ? $conf['limit'] : 128);
