        $scheme = $request->getUri()->getScheme();
        $host = $request->getUri()->getHost();
        $port = $request->getUri()->getPort() ?: ($scheme === 'https' ? 443 : 80);
        $path = $request->getUri()->getPath();
        $query = $request->getUri()->getQuery() ? '?' . $request->getUri()->getQuery() : '';
        $headers = $request->getHeaders();
        $body = $request->getBody();

        $socket = fsockopen($host, $port, $errno, $errstr, 30);
        if (!$socket) {
            $this->logger->error("Failed to connect to {$host}:{$port} - {$errstr}");
            throw new \Exception("Connection failed");
        }

        $headerString = "GET {$path}{$query} HTTP/1.1\r\n";
        $headerString .= "Host: {$host}\r\n";
        foreach ($headers as $name => $values) {
            foreach ($values as $value) {
                $headerString .= "$name: $value\r\n";
            }
        }
        $headerString .= "\r\n";

        fwrite($socket, $headerString);
        if (!$body->isEmpty()) {
            fwrite($socket, $body->getContents());
        }

        $response = '';
        while (!feof($socket)) {
            $response .= fgets($socket, 128);
        }

        fclose($socket);

        return new \GuzzleHttp\Psr7\Response(
            preg_match('/HTTP\/1\.1 (\d+)/', $response, $matches) ? $matches[1] : 500,
            array_map(function($line) { return explode(': ', trim($line)); }, array_filter(explode("\r\n", substr($response, strpos($response, "\r\n\r\n") + 4)), 'strlen')),
            new \GuzzleHttp\Psr7\Stream(fopen('php://memory', 'rw'), 'wb+'),
            null,
            $response
        );
    }
}
