    $connection = new AMQPStreamConnection($host, $port, $user, $pass, $vhost);
    $channel = $connection->channel();

    $channel->exchange_declare($exchange, 'direct', false, true, false);

    list($queue_name, ,) = $channel->queue_declare(
        $queue,
        false,
        true,
        false,
        false
    );

    $channel->queue_bind($queue_name, $exchange, 'routing_key');

    echo " [*] Waiting for messages. To exit press CTRL+C\n";

    $callback = function ($msg) use ($channel) {
        if ($msg->body === 'quit') {
            echo " [x] Received quit signal, exiting...\n";
            $channel->basic_cancel($msg->getConsumerTag());
            return;
        }

        if ($msg->body === 'good') {
            echo " [x] Received good message, acknowledging...\n";
            $msg->delivery_info['channel']->basic_ack($msg->delivery_info['delivery_tag']);
        } else {
            echo " [x] Received bad message, rejecting...\n";
            $msg->delivery_info['channel']->basic_nack($msg->delivery_info['delivery_tag'], false, true);
        }
    };

    $channel->basic_consume($queue_name, '', false, false, false, false, $callback);

    register_shutdown_function(function () use ($channel, $connection) {
        $channel->close();
        $connection->close();
    });

    while ($channel->is_consuming()) {
        $channel->wait();
    }
}

