function send_rabbitmq_messages($max_messages, $host, $port, $user, $pass, $vhost, $message_body)
{
    use PhpAmqpLib\Connection\AMQPStreamConnection;
    use PhpAmqpLib\Message\AMQPMessage;

    $conn = new AMQPStreamConnection($host, $port, $user, $pass, $vhost);
    if (!$conn) {
        return false;
    }

    $time = microtime(true);

    for ($i = 0; $i < $max_messages; $i++) {
        $ch = $conn->channel();
        list($queue,) = $ch->queue_declare('', false, false, true, true);
        $msg = new AMQPMessage($message_body);
        $ch->basic_publish($msg, '', $queue);
        $ch->close();
    }

    $execution_time = microtime(true) - $time;
    $conn->close();

    return $execution_time;
}

