    $channel->queue_declare($queue_name, false, true, false, false);

    $start_time = microtime(true);
    $messages_per_batch = floor($msg_count / $batch_size);
    $remaining_messages = $msg_count % $batch_size;

    for ($i = 0; $i < $messages_per_batch; $i++) {
        $batch = [];
        for ($j = 0; $j < $batch_size; $j++) {
            $message = new AMQPMessage('Hello World!');
            $channel->basic_publish($message, $exchange_name, $queue_name);
            array_push($batch, $message);
        }
        $channel->wait_for_pending_acks();
    }

    if ($remaining_messages > 0) {
        $batch = [];
        for ($j = 0; $j < $remaining_messages; $j++) {
            $message = new AMQPMessage('Hello World!');
            $channel->basic_publish($message, $exchange_name, $queue_name);
            array_push($batch, $message);
        }
        $channel->wait_for_pending_acks();
    }

    $channel->basic_publish(new AMQPMessage('quit'), $exchange_name, $queue_name);
    $channel->wait_for_pending_acks();

    $end_time = microtime(true);
    $total_execution_time = $end_time - $start_time;
    $channel->close();
    $connection->close();

